mapper.insert:
insert into tbl_tran( id, owner, money, name, expectedDate, customerId, stage, type, source, activityId, contactsId, createBy, createTime, description, contactSummary, nextContactTime
)values (
#{id}, #{owner}, #{money}, #{name}, #{expectedDate}, #{customerId}, #{stage}, #{type}, #{source}, #{activityId}, #{contactsId}, #{createBy}, #{createTime}, #{description}, #{contactSummary}, #{nextContactTime} )

mapper.select:
select
t.id, u.name as owner, t.money, t.name, t.expectedDate, c.name as customerId, t.stage, t.type, t.source, a.name as activityId, con.fullname as contactsId, t.createBy, t.createTime, t.editBy, t.editTime, t.description, t.contactSummary, t.nextContactTime
from tbl_tran t join tbl_user u on t.owner=u.id join tbl_customer c on t.customerId=c.id left join tbl_activity a on t.activityId = a.id join tbl_contacts con on t.contactsId = con.id where t.id=#{id}

mapper.update:
update tbl_tran

    set stage=#{stage},
    editBy=#{editBy},
    editTime=#{editTime}

    where id=#{id}
